import { contactPageData } from '../actions/contact';
import TopMostCategories from './top-most-categories';
import axios from 'axios';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

const Home = () => {
  const topMostCategories = [
    {
      name: 'Men',
      key: 'men',
      route: '/men',
      component: Men
    },
    {
      name: 'Women',
      key: 'women',
      route: '/women',
      component: Women
    },
    {
      name: 'Accessorries',
      key: 'accessorries',
      route: '/accessories',
      component: Accessories
    },
    {
      name: 'Category A',
      key: 'categoryA',
      route: '/category-A',
      component: CategoryA
    },
    {
      name: 'Category B',
      key: 'categoryB',
      route: '/category-B',
      component: CategoryB
    },
    {
      name: 'CategoryC',
      key: 'categoryC',
      route: '/category-C',
      component: CategoryC
    }
  ];
  return (
    <Fragment>
      <div className='badydiv'>
        <div className='universalDiv'>
          <div className='container'>
            <Navbar></Navbar>
          </div>
          <section className='bodypanelmian'>
            <div className='container'>
              <TopMostCategories data={topMostCategories} />
              <Router>
                <Switch>
                  {
                    topMostCategories && topMostCategories.length > 0 && topMostCategories.map(category => {
                      <Route key={category.key} exact path={category.route} component={category.component} />
                    })
                  }
                </Switch>
              </Router>
              <Footer />
            </div>
          </section>
        </div>
      </div>
    </Fragment>
  )
}

