import { Link, useHistory } from 'react-router-dom';
import style from './top-most-categories.scss';
import { isNotEmptyArray } from '../utils/global-functions';

function TopMostCategories({ data, location }) {
    const history = useHistory();
    // const location = useLocation();
    const pathname = location.pathname.toString();
    const pathValue = pathname.lastIndexOf('/' + 1);

    return (
        <div className='top-most-categories '>
            {
                isNotEmptyArray(data) &&
                <ul className='d-flex align-items-center'>
                    <li>
                        {
                            data.map(data => {
                                <Link to={{ pathname: data.route, state: location.state }}
                                    className={pathValue === data.route.lastIndexOf('/' + 1) ? 'active' : ''}
                                >
                                    {data.name}
                                </Link>
                            })
                        }
                    </li>
                </ul>
            }
        </div>
    )
}

